import React, { useState, useEffect } from 'react';
import { Box, Flex, Button, Select, Input, Image } from '@chakra-ui/react';
import { Link } from 'react-router-dom';

function Flights() {
    const [flights, setFlights] = useState([]);
    const [filters, setFilters] = useState({
        departureCountry: '',
        departureCity: '',
        departureAirport: '',
        arrivalCountry: '',
        arrivalCity: '',
        arrivalAirport: '',
        date: '',
    });

    useEffect(() => {
        fetchFlights(); // İlk yüklemede tüm uçuşları getir
    }, []);

    const fetchFlights = async () => {
        const query = new URLSearchParams(filters).toString();
        try {
            const response = await fetch(`http://localhost:8085/flights?${query}`);
            const data = await response.json();
            setFlights(data);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    const handleFilterChange = (e) => {
        setFilters({ ...filters, [e.target.name]: e.target.value });
    };

    const handleSearch = () => {
        fetchFlights(); // Filtrelenmiş uçuşları getir
    };

    return (
        <div>
            {/* Filtreleme UI Bölümü */}
            <Box p="4" borderWidth="1px" borderRadius="lg" overflow="hidden" mb="20px">
                <Flex mb="20px" justifyContent="center" gridGap="20px">
                    {/* Filtreleme seçenekleri */}
                    {/* ... */}
                    <Button colorScheme="blue" onClick={handleSearch}>Ara</Button>
                </Flex>
            </Box>

            {/* Uçuş Listesi */}
            {flights.map(flight => (
                <div key={flight.id}>
                    <Link to={`/flight/${flight.id}`}>
                        {/* Uçuş bilgileri */}
                    </Link>
                </div>
            ))}
        </div>
    );
}

export default Flights;
